//office space
const modelOffice = [];
gltfLoader.load( 'models/office_space/model_0.gltf', function ( gltf ) {
    let model = gltf.scene;
    model.scale.set(10,10,10);
    model.position.set(0,-10,0);
 scene.add( model );

    model.traverse(function(child){
        if(child.isMesh){
            modelOffice.push(child);
        }
    });
}, undefined, function ( error ) {
 console.error( error );
});
